import React, { useState } from 'react';
       import { Button } from '@/components/ui/button'; // If you are using shadcn/ui
       import { Input } from '@/components/ui/input';  // If you are using shadcn/ui
       import { Label } from '@/components/ui/label'; //If you are using shadcn/ui
       import { cn } from "@/lib/utils" //If you are using shadcn/ui

       const Register = () => {
         const [username, setUsername] = useState('');
         const [email, setEmail] = useState('');
         const [password, setPassword] = useState('');
         const [message, setMessage] = useState('');
         const [error, setError] = useState('');
         const [isLoading, setIsLoading] = useState(false);

         const handleSubmit = async (e) => {
           e.preventDefault();
           setIsLoading(true);
           setError('');
           setMessage('');

           try {
             const response = await fetch('http://localhost:5000/register', { // Use your backend URL
               method: 'POST',
               headers: {
                 'Content-Type': 'application/json',
               },
               body: JSON.stringify({ username, email, password }),
             });

             const data = await response.json();

             if (response.ok) {
               setMessage(data.message);
               // Redirect to login or home page upon successful registration
               // For example:  window.location.href = '/login';
             } else {
               setError(data.message || 'Registration failed');
             }
           } catch (err) {
             setError(err.message || 'An error occurred');
           } finally {
             setIsLoading(false);
           }
         };

         return (
           <div className="flex items-center justify-center min-h-screen bg-gray-100">
             <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
               <h2 className="text-2xl font-bold mb-6 text-center">Register</h2>
               <form onSubmit={handleSubmit} className="space-y-4">
                 <div>
                   <Label htmlFor="username">Username</Label>
                   <Input
                     type="text"
                     id="username"
                     value={username}
                     onChange={(e) => setUsername(e.target.value)}
                     required
                     className={cn(error && "border-red-500")}
                     placeholder="Enter your username"
                   />
                 </div>
                 <div>
                   <Label htmlFor="email">Email</Label>
                   <Input
                     type="email"
                     id="email"
                     value={email}
                     onChange={(e) => setEmail(e.target.value)}
                     required
                     className={cn(error && "border-red-500")}
                     placeholder="Enter your email"
                   />
                 </div>
                 <div>
                   <Label htmlFor="password">Password</Label>
                   <Input
                     type="password"
                     id="password"
                     value={password}
                     onChange={(e) => setPassword(e.target.value)}
                     required
                     className={cn(error && "border-red-500")}
                     placeholder="Enter your password"
                   />
                 </div>
                 <Button type="submit" className="w-full" disabled={isLoading}>
                   {isLoading ? 'Loading...' : 'Register'}
                 </Button>
               </form>
               {message && <p className="mt-4 text-green-500 text-center">{message}</p>}
               {error && <p className="mt-4 text-red-500 text-center">{error}</p>}
             </div>
           </div>
         );
       };

       export default Register;
